devs = {}

file.each do |project, gem_list|
  # puts gem_list
  gem_list.each do |gem_author|
     a = gem_author["author"]
     g = gem_author["gem"]

     devs[a] = {} if !devs.keys.include? a

     devs[a].keys.include?(g) ? devs[a][g] += 1 : devs[a][g] = 1

  end
end

devs.delete("MildoOSJ")

devs.each do |dev, gems|
  puts "-----" + dev + "-----"
  puts "\nGEMS:\n\n"

  i = 0

  gems.each do |g, c|
    puts "#{g}: #{c}\n"
    i += 1
  end

  puts "\n"
  puts "TOTAL: #{i}"
  puts "\n"

end
